import React, { useState, useRef } from "react";
import { useSpeechToText } from "../hooks/useSpeechToText";

const AudioHybridRecorder = () => {
  // Toggles
  const [useWebSpeech, setUseWebSpeech] = useState(true);
  const [useWhisper, setUseWhisper] = useState(true);
  const [useLlama, setUseLlama] = useState(true);

  // Recording state
  const [recording, setRecording] = useState(false);
  const [audioBlob, setAudioBlob] = useState<Blob | null>(null);
  const [finalTranscript, setFinalTranscript] = useState("");
  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
  const audioChunksRef = useRef<Blob[]>([]);

  // Web Speech API transcript
  const { transcript, listening, startListening, stopListening, isBrowserSupported } = useSpeechToText("en-US");

  // Start recording
  const handleStartRecording = async () => {
    setAudioBlob(null);
    setFinalTranscript("");
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    const mediaRecorder = new MediaRecorder(stream);
    mediaRecorderRef.current = mediaRecorder;
    audioChunksRef.current = [];

    mediaRecorder.ondataavailable = (event: BlobEvent) => {
      audioChunksRef.current.push(event.data);
    };

    mediaRecorder.onstop = () => {
      const audioBlob = new Blob(audioChunksRef.current, { type: "audio/wav" });
      setAudioBlob(audioBlob);
      stream.getTracks().forEach((track) => track.stop());
    };

    mediaRecorder.start();
    setRecording(true);
  };

  // Stop recording
  const handleStopRecording = () => {
    if (mediaRecorderRef.current) {
      mediaRecorderRef.current.stop();
      setRecording(false);
    }
  };

  // Transcribe
  const handleTranscribe = async () => {
    if (!audioBlob && useWhisper) {
      alert("Please record some audio first.");
      return;
    }

    const url = `http://localhost:8080/api/transcribe?use_webspeech=${useWebSpeech ? 1 : 0}&use_whisper=${useWhisper ? 1 : 0}&use_llama=${useLlama ? 1 : 0}`;
    const formData = new FormData();
    if (audioBlob && useWhisper) {
      formData.append("audio", audioBlob, "audio.wav");
    } else {
      formData.append("audio", new Blob(), "blank.wav");
    }
    // Use the Web Speech API transcript
    formData.append("web_transcript", transcript);

    const res = await fetch(url, { method: "POST", body: formData });
    const data = await res.json();
    setFinalTranscript(data.final_transcript || "");
  };

  return (
    <div>
      <h2>Transcription Settings</h2>
      <label>
        <input type="checkbox" checked={useWebSpeech} onChange={() => setUseWebSpeech(v => !v)} />
        Web Speech
      </label>
      <label style={{ marginLeft: 16 }}>
        <input type="checkbox" checked={useWhisper} onChange={() => setUseWhisper(v => !v)} />
        Whisper
      </label>
      <label style={{ marginLeft: 16 }}>
        <input type="checkbox" checked={useLlama} onChange={() => setUseLlama(v => !v)} />
        Llama
      </label>
      <br /><br />
      <button onClick={handleStartRecording} disabled={recording}>
        Start Recording
      </button>
      <button onClick={handleStopRecording} disabled={!recording}>
        Stop Recording
      </button>

      {/* Web Speech Controls */}
      <button onClick={startListening} disabled={listening || !isBrowserSupported}>
        Start Web Speech
      </button>
      <button onClick={stopListening} disabled={!listening}>
        Stop Web Speech
      </button>
      <textarea value={transcript} readOnly placeholder="Web Speech Transcript" />

      <br /><br />
      <button onClick={handleTranscribe}>Transcribe</button>
      <br /><br />
      <textarea
        value={finalTranscript}
        readOnly
        style={{ width: "100%", minHeight: 120 }}
        placeholder="Transcript output here"
      />
      <br />
      {audioBlob && (
        <audio controls src={URL.createObjectURL(audioBlob)} />
      )}
    </div>
  );
};

export default AudioHybridRecorder;
