import React, { useState, useRef } from "react";

const AudioRecorder: React.FC = () => {
  const [isRecording, setIsRecording] = useState(false);
  const [transcript, setTranscript] = useState("");
  const recognitionRef = useRef<any>(null);

  const SpeechRecognition =
    (window as any).SpeechRecognition || (window as any).webkitSpeechRecognition;

  const startRecording = () => {
    if (!SpeechRecognition) {
      alert("Speech Recognition API not supported in this browser.");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.continuous = true;
    recognition.interimResults = true;
    recognition.lang = "en-US";

    recognition.onresult = (event: any) => {
      let interim = "";
      for (let i = event.resultIndex; i < event.results.length; ++i) {
        const result = event.results[i];
        if (result.isFinal) {
          setTranscript((prev) => prev + result[0].transcript + " ");
        } else {
          interim += result[0].transcript;
        }
      }
      if (interim) setTranscript((prev) => prev.split(" [INTERIM]")[0] + interim + " [INTERIM]");
    };

    recognition.onerror = (e: any) => {
      console.error("Speech recognition error:", e);
      setIsRecording(false);
      recognition.stop();
    };

    recognition.onend = () => {
      setIsRecording(false);
    };

    recognitionRef.current = recognition;
    recognition.start();
    setIsRecording(true);
  };

  const stopRecording = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop();
      recognitionRef.current = null;
    }
    setIsRecording(false);
    setTranscript((prev) => prev.replace(/\s?\[INTERIM\]$/, ""));
  };

  const clearTranscript = () => setTranscript("");

  return (
    <div style={{ marginTop: 24 }}>
      <button
        onClick={isRecording ? stopRecording : startRecording}
        style={{
          background: isRecording ? "#e53935" : "#388e3c",
          color: "#fff",
          border: "none",
          padding: "8px 20px",
          borderRadius: 6,
          marginBottom: 16,
          fontWeight: "bold",
          marginRight: 10,
        }}
      >
        {isRecording ? "Stop Recording" : "Start Recording"}
      </button>
      <button
        onClick={clearTranscript}
        style={{
          background: "#b0b0b0",
          color: "#222",
          border: "none",
          padding: "8px 20px",
          borderRadius: 6,
          marginBottom: 16,
          fontWeight: "bold",
        }}
        disabled={!transcript}
      >
        Clear
      </button>
      <div>
        <textarea
          value={transcript.replace(/\s?\[INTERIM\]$/, "")}
          rows={8}
          style={{
            width: "98%",
            fontSize: "1.4em",
            border: "2px solid #e53935",
            borderRadius: 8,
            padding: 12,
            marginTop: 12,
            color: "#333",
            background: "#fffdfc",
          }}
          placeholder="Auto transcribe here..."
          readOnly
        />
      </div>
    </div>
  );
};

export default AudioRecorder;
