import React, { useState, useRef } from "react";
import { useSpeechToText } from "../hooks/useSpeechToText";

// === Web Speech Box ===
const WebSpeechBox = () => {
  const { transcript, listening, startListening, stopListening, isBrowserSupported, clearTranscript } = useSpeechToText("en-US");

  return (
    <div style={{ border: "2px solid #0074d9", borderRadius: 8, padding: 16, width: 350, marginBottom: 18 }}>
      <h3>Web Speech</h3>
      <button onClick={startListening} disabled={listening || !isBrowserSupported}>Start</button>
      <button onClick={stopListening} disabled={!listening}>Stop</button>
      <button onClick={clearTranscript}>Clear</button>
      <br />
      <textarea
        value={transcript}
        readOnly
        style={{ width: "100%", minHeight: 60, marginTop: 8 }}
        placeholder="Web Speech Output"
      />
    </div>
  );
};

// === Whisper Box ===
const WhisperBox = () => {
  const [recording, setRecording] = useState(false);
  const [audioBlob, setAudioBlob] = useState<Blob | null>(null);
  const [output, setOutput] = useState("");
  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
  const audioChunksRef = useRef<Blob[]>([]);

  const handleStart = async () => {
    setOutput("");
    setAudioBlob(null);
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    const recorder = new MediaRecorder(stream);
    mediaRecorderRef.current = recorder;
    audioChunksRef.current = [];

    recorder.ondataavailable = (e: BlobEvent) => audioChunksRef.current.push(e.data);
    recorder.onstop = () => {
      const blob = new Blob(audioChunksRef.current, { type: "audio/wav" });
      setAudioBlob(blob);
      stream.getTracks().forEach(track => track.stop());
    };
    recorder.start();
    setRecording(true);
  };

  const handleStop = () => {
    if (mediaRecorderRef.current) {
      mediaRecorderRef.current.stop();
      setRecording(false);
    }
  };

  const handleTranscribe = async () => {
    if (!audioBlob) return alert("Record audio first!");
    setOutput("Transcribing...");
    const formData = new FormData();
    formData.append("audio", audioBlob, "audio.wav");
    formData.append("web_transcript", ""); // not used for Whisper
    const res = await fetch("http://localhost:8080/api/transcribe?use_webspeech=0&use_whisper=1&use_llama=0", {
      method: "POST",
      body: formData
    });
    const data = await res.json();
    setOutput(data.whisper_transcript || data.final_transcript || "[no output]");
  };

  const handleClear = () => {
    setAudioBlob(null);
    setOutput("");
  };

  return (
    <div style={{ border: "2px solid #2ecc40", borderRadius: 8, padding: 16, width: 350, marginBottom: 18 }}>
      <h3>Whisper</h3>
      <button onClick={handleStart} disabled={recording}>Record</button>
      <button onClick={handleStop} disabled={!recording}>Stop</button>
      <button onClick={handleClear}>Clear</button>
      <button onClick={handleTranscribe} disabled={!audioBlob}>Transcribe</button>
      <br />
      <textarea
        value={output}
        readOnly
        style={{ width: "100%", minHeight: 60, marginTop: 8 }}
        placeholder="Whisper Output"
      />
      {audioBlob && <audio controls src={URL.createObjectURL(audioBlob)} />}
    </div>
  );
};

// === Llama Box (merges Whisper and Web Speech transcripts) ===
const LlamaBox = () => {
  const [webTranscript, setWebTranscript] = useState("");
  const [whisperTranscript, setWhisperTranscript] = useState("");
  const [llamaOutput, setLlamaOutput] = useState("");

  const handleLlama = async () => {
    if (!webTranscript && !whisperTranscript) {
      alert("Paste or type at least one transcript!");
      return;
    }
    setLlamaOutput("Processing...");
    // Dummy audio for API requirement (not actually used here)
    const formData = new FormData();
    formData.append("audio", new Blob(), "blank.wav");
    formData.append("web_transcript", webTranscript);

    // Send both transcripts to backend for Llama "merging/refining"
    const res = await fetch("http://localhost:8080/api/transcribe?use_webspeech=1&use_whisper=1&use_llama=1", {
      method: "POST",
      body: formData
    });
    const data = await res.json();
    setLlamaOutput(data.final_transcript || "[no output]");
  };

  const handleClear = () => {
    setWebTranscript("");
    setWhisperTranscript("");
    setLlamaOutput("");
  };

  return (
    <div style={{ border: "2px solid #b10dc9", borderRadius: 8, padding: 16, width: 350, marginBottom: 18 }}>
      <h3>Llama</h3>
      <textarea
        value={webTranscript}
        onChange={e => setWebTranscript(e.target.value)}
        placeholder="Paste Web Speech transcript"
        style={{ width: "100%", minHeight: 30, marginBottom: 4 }}
      />
      <textarea
        value={whisperTranscript}
        onChange={e => setWhisperTranscript(e.target.value)}
        placeholder="Paste Whisper transcript"
        style={{ width: "100%", minHeight: 30, marginBottom: 8 }}
      />
      <br />
      <button onClick={handleLlama}>Llama Transcribe</button>
      <button onClick={handleClear} style={{ marginLeft: 8 }}>Clear</button>
      <br />
      <textarea
        value={llamaOutput}
        readOnly
        style={{ width: "100%", minHeight: 60, marginTop: 8 }}
        placeholder="Llama Output (merged/refined)"
      />
    </div>
  );
};

// === Main Dashboard Layout ===
const AudioTranscriptionDashboard = () => (
  <div>
    <h1>Medical E.V.A.N.S</h1>
    <h2>Medical- Empathetic Voice-over Assistant for Nurturing Symptomatologist</h2>
    <h3>Transcription Components:</h3>
    <div style={{ display: "flex", gap: 16, flexWrap: "wrap" }}>
      <WebSpeechBox />
      <WhisperBox />
      <LlamaBox />
    </div>
  </div>
);

export default AudioTranscriptionDashboard;
